db:select.db:parameterized-query:
SELECT EXTRACT(year_month from created_on), UNIX_TIMESTAMP(MIN(created_on)) x, COUNT(1) total_registered, SUM(CASE WHEN carrier_type_id = 1 THEN 1 ELSE 0 END) audio_registered, SUM(CASE WHEN carrier_type_id = 2 THEN 1 ELSE 0 END) video_registered, SUM(CASE WHEN carrier_type_id = 3 THEN 1 ELSE 0 END) paper_registered, SUM(CASE WHEN carrier_type_id = 4 THEN 1 ELSE 0 END) film_registered
FROM ${ams.db.carrier}
WHERE organization_id IN (SELECT id FROM ${ams.db.organizations} WHERE noid_id = #[flowVars.cp_ldap])
GROUP BY EXTRACT(year_month from created_on)
ORDER BY MIN(created_on) ASC;

